formalpara.programlisting:
CREATE TYPE SexType AS ENUM ('female', 'male', 'unknown');
 CREATE TABLE people (
 	"aacode"		varchar(100),
 	"age"		int,
 	"sex"		SexType,
 	"smoker"		boolean
 );

CREATE TYPE CoveringType AS ENUM ('tile', 'slates', 'thatch', 'asphalt', 'metal',
    'felt');
 CREATE TYPE RoofConstructionType AS ENUM ('pitchedslateortiles', 'thatched', 'flat');
 CREATE TYPE RoofStructureType AS ENUM ('pitched', 'flat', 'mandard', 'chalet');
 CREATE TABLE roofs (
 	"aacode"		varchar(100),
 	"coveringtype"		CoveringType,
 	"insulationthickness"		double precision,
 	"contructiontype"		RoofConstructionType,
 	"structuretype"		RoofStructureType
 );

CREATE TABLE lighting (
 	"aacode"		varchar(100),
 	"fraction"		double precision
 );
